mapper.select:
select * from fxy_financial_voucher_details_auxiliary ffvda
inner join  fxy_financial_accounting_category_details  ffacd on ffvda.accounting_category_details_id = ffacd.id
where ffvda.voucher_details_id = #{voucherDetailsId,jdbcType=INTEGER}

select * from fxy_financial_accounting_category_details ffacd where ffacd.id in (
select distinct ffvda.accounting_category_details_id
from fxy_financial_voucher_details_auxiliary ffvda
inner join fxy_financial_voucher_details ffvd on ffvda.voucher_details_id = ffvd.id
inner join fxy_financial_voucher ffv on ffvd.voucher_id = ffv.id
where ffv.account_sets_id=#{accountSetsId,jdbcType=INTEGER} and ffvda.accounting_category_id = #{auxiliaryId,jdbcType=INTEGER}

    and ffv.org_id = #{orgId,jdbcType=INTEGER}

)

select ffv.voucher_date, ffv.word, ffv.code, ffvd.voucher_id, ffvd.summary, ffvd.debit_amount, ffvd.credit_amount, ffs.balance_direction from fxy_financial_voucher_details ffvd inner join fxy_financial_voucher ffv on ffvd.voucher_id = ffv.id left join fxy_financial_subject ffs on ffvd.subject_id = ffs.id left join fxy_financial_voucher_details_auxiliary ffvda on ffvd.id = ffvda.voucher_details_id where ffv.account_sets_id = #{accountSetsId,jdbcType=INTEGER} and ffvda.accounting_category_id = #{auxiliaryId,jdbcType=INTEGER} and ffvda.accounting_category_details_id = #{auxiliaryItemId,jdbcType=INTEGER,jdbcType=INTEGER} and date_format(ffv.voucher_date, '%Y%m') = date_format(#{accountDate}, '%Y%m')
and ffv.org_id = #{orgId,jdbcType=INTEGER}
order by ffvd.voucher_id

select

    ffvda.accounting_category_details_id detailsId,

sum(ffvd.debit_amount) debit_amount,
sum(ffvd.credit_amount) credit_amount,
ffs.balance_direction
from fxy_financial_voucher_details ffvd
inner join fxy_financial_voucher ffv on ffvd.voucher_id = ffv.id
left join fxy_financial_subject ffs on ffvd.subject_id = ffs.id
left join fxy_financial_voucher_details_auxiliary ffvda on ffvd.id = ffvda.voucher_details_id
where ffv.account_sets_id = #{accountSetsId,jdbcType=INTEGER} and ffvd.voucher_id is not null
and ffvda.accounting_category_id = #{auxiliaryId,jdbcType=INTEGER}

    and ffvda.accounting_category_details_id = #{auxiliaryItemId,jdbcType=INTEGER,jdbcType=INTEGER}


    and ffv.voucher_date between #{startDate,jdbcType=TIMESTAMP} and #{endDate,jdbcType=TIMESTAMP}


    and ffv.voucher_date >= #{startDate,jdbcType=TIMESTAMP}


    and ffv.voucher_date <  #{endDate,jdbcType=TIMESTAMP}


    and ffvd.org_id = #{orgId,jdbcType=INTEGER}


    group by ffvda.accounting_category_details_id
